<?php class wpSEO_Base{public static function get_lang($short=false){$output=get_locale(); if($lang=wpSEO_Options::get('misc_lang')){if(in_array($lang, array('de_DE', 'en_EN'))){$output=$lang;}}if($short){return($output=='de_DE' ? 'de' : 'en');}return $output;}public static function get_meta($field){return get_post_meta($GLOBALS['wp_query']->get_queried_object_id(), $field, true);}public static function get_host($url){preg_match('@^(?:https?://)?([^/]+)@i',(string)$url, $matches); return @$matches[1];}public static function get_file($url, $method='get', $args=array('timeout'=> 30)){if(empty($url)or !in_array($method, array('get', 'post', 'request'))or !is_callable('wp_remote_' .$method)){return new WP_Error('http_request_failed', __('Empty url or unauthorized method type'));}$response=call_user_func('wp_remote_' .$method, esc_url($url, array('http', 'https'), 'url'), $args); if(is_wp_error($response)){return $response;}if(wp_remote_retrieve_response_code($response)!=200){return new WP_Error('http_request_failed', sprintf(__('Could not open: %s'), $url));}return wp_remote_retrieve_body($response);}}