<?php class wpSEO_Tax{public static function init(){if(wpSEO_Feedback::get('critical')){return;}if(!empty($_POST['taxonomy'])){add_action('edit_' .$_POST['taxonomy'], array('wpSEO_Tax', 'update'));}elseif($tax=$GLOBALS['taxnow']){add_action($tax. '_edit_form_fields', array('wpSEO_Tax', 'show'));}}private static function _get($term){return get_option(sprintf('wpseo_%s_%d', $term->taxonomy, $term->term_id));}public static function update($term_id){if(!isset($_POST['wpseo_tax_short'])or empty($_POST['taxonomy'])){return $term_id;}if($term=get_term($term_id, $_POST['taxonomy'])){update_option(sprintf('wpseo_%s_%d', $term->taxonomy, $term->term_id), sanitize_text_field($_POST['wpseo_tax_short']));}return $term_id;}public static function show($term){?>		<tr class="form-field">			<th scope="row" valign="top">				<label><?php esc_html_e('Short description', 'wpseo')?></label>			</th>			<td>				<input type="text" name="wpseo_tax_short" value="<?php echo esc_attr(self::_get($term))?>">				<br />				<span class="description"><?php esc_html_e('The short description can be used in wpSEO plugin.', 'wpseo')?></span>			</td>		</tr>	<?php }}